mapper.select:
SELECT 
u.id id
,u.login_id loginId
,u.password 
,u.first_name firstName
,u.last_name lastName
,u.valid_from validFrom
,u.valid_to validTo
,u.isactive isactive
,u.create_by createBy
,u.create_on createOn
,u.last_update_by lastUpdateBy
,u.last_update_on lastUpdateOn
,u.default_netgrp defaultNetGrp
,u.landing_page landingPage
,u.locale locale
FROM spo_user u
WHERE 1 =1 

        and u.id = #{id}


        and u.login_id = #{loginId}


        and u.password = #{password}


       and  u.first_name = #{firstName}


        and u.last_name = #{lastName}


        and u.valid_from = #{validFrom}


        and u.valid_to = #{validTo}


        and u.isactive = #{isactive}


        and u.create_by = #{createBy}


        and u.create_on = #{createOn}


        and u.last_update_by = #{lastUpdateBy}


        and u.last_update_on = #{lastUpdateOn}


        and u.default_netgrp =  #{defaultNetGrp,jdbcType=VARCHAR}


        and u.landing_page =   #{landingPage,jdbcType=VARCHAR}


        and u.locale =   #{locale,jdbcType=VARCHAR}

SELECT 
r.id id
,r.role_id roleId
,r.role_name roleName
,r.role_description roleDescription
,r.valid_from validFrom
,r.valid_to validTo
,r.isactive isactive
,r.role_type roleType
,r.create_by createBy
,r.create_on createOn
,r.last_update_by lastUpdateBy
,r.last_update_on lastUpdateOn

FROM
spo_user u 
LEFT JOIN rel_user_role rel
ON  u.id = rel.user_id
LEFT JOIN spo_role r
ON  r.role_id = rel.role_id
WHERE 1 =1 

        and u.id = #{id}


        and u.login_id = #{loginId}


        and u.password = #{password}


       and  u.first_name = #{firstName}


        and u.last_name = #{lastName}


        and u.valid_from = #{validFrom}


        and u.valid_to = #{validTo}


        and u.isactive = #{isactive}


        and u.create_by = #{createBy}


        and u.create_on = #{createOn}


        and u.last_update_by = #{lastUpdateBy}


        and u.last_update_on = #{lastUpdateOn}

